import React, { useState } from 'react';
import { Box, TextField, Button, Typography, CircularProgress } from '@mui/material';

const TovarForm = ({ onAddTovar, isLoading }) => {
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [image, setImage] = useState('');
  const [genre, setGenre] = useState('');
  const [releaseDate, setReleaseDate] = useState('');
  const [rating, setRating] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!title.trim() || !description.trim() || !image.trim() || !genre.trim() || !releaseDate) {
      alert('Please fill in all fields');
      return;
    }

    if (isNaN(rating) || rating < 0) {
      alert('Rating must be a positive number');
      return;
    }

    if (isNaN(Date.parse(releaseDate))) {
      alert('Invalid release date');
      return;
    }

    const newTovar = {
      title,
      description,
      image,
      genre: genre ? genre.split(',').map((g) => g.trim()) : [],
      releaseDate,
      rating: parseFloat(rating) || 0,
    };

    try {
      await onAddTovar(title, description, image, genre, releaseDate, rating);
      setTitle('');
      setDescription('');
      setImage('');
      setGenre('');
      setReleaseDate('');
      setRating('');
    } catch (error) {
      alert('Error adding movie');
    }
  };

  return (
    <Box component="form" onSubmit={handleSubmit} sx={{ mt: 4 }}>
      <Typography variant="h6" gutterBottom>Add New Movie</Typography>

      <TextField fullWidth label="Title" variant="outlined" value={title} onChange={(e) => setTitle(e.target.value)} required margin="normal" />
      <TextField fullWidth label="Description" variant="outlined" multiline value={description} onChange={(e) => setDescription(e.target.value)} required margin="normal" />
      <TextField fullWidth label="Image URL" variant="outlined" value={image} onChange={(e) => setImage(e.target.value)} required margin="normal" />
      <TextField fullWidth label="Genre" variant="outlined" value={genre} onChange={(e) => setGenre(e.target.value)} required margin="normal" />
      <TextField fullWidth label="Release Date" type="date" variant="outlined" value={releaseDate} onChange={(e) => setReleaseDate(e.target.value)} required margin="normal" InputLabelProps={{ shrink: true }} />
      <TextField fullWidth label="Rating" type="number" variant="outlined" value={rating} onChange={(e) => setRating(e.target.value)} step="0.1" margin="normal" />

      <Button type="submit" fullWidth variant="contained" color="primary" disabled={isLoading} sx={{ mt: 2 }} startIcon={isLoading && <CircularProgress size={20} />}>
        {isLoading ? 'Adding...' : 'Add Movie'}
      </Button>
    </Box>
  );
};

export default TovarForm;
