import React, { useState, useContext } from 'react';
import { Box, TextField, Button, Typography, CircularProgress } from '@mui/material';
import { AuthContext } from '../contexts/AuthContext'; 
import API_BASE_URL from '../config';

const TovarForm = ({ onAddTovar, isLoading }) => {
  const { token } = useContext(AuthContext); // Получаем токен из контекста
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [image, setImage] = useState('');
  const [genre, setGenre] = useState('');
  const [releaseDate, setReleaseDate] = useState('');
  const [rating, setRating] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!title.trim() || !description.trim() || !image.trim() || !genre.trim() || !releaseDate) {
      alert('Please fill in all fields');
      return;
    }

    const newTovar = {
      title,
      description,
      image,
      genre: genre.split(',').map((g) => g.trim()),
      releaseDate,
      rating: parseFloat(rating) || 0,
    };

    try {
      const response = await fetch(`${API_BASE_URL}/tovars`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`, // Добавляем токен
        },
        body: JSON.stringify(newTovar),
      });

      const data = await response.json();
      if (data.success) {
        onAddTovar(data.data);
        setTitle('');
        setDescription('');
        setImage('');
        setGenre('');
        setReleaseDate('');
        setRating('');
      } else {
        alert(data.message);
      }
    } catch (error) {
      console.error('Unexpected error:', error);
      alert('Unexpected error. Please try again.');
    }
  };

  return (
    <Box component="form" onSubmit={handleSubmit} sx={{ mt: 4 }}>
      <Typography variant="h6" gutterBottom>Add New Movie</Typography>

      <TextField
        fullWidth
        label="Title"
        variant="outlined"
        value={title}
        onChange={(e) => setTitle(e.target.value)}
        required
        margin="normal"
        InputProps={{
          style: { borderRadius: 8 },
        }}
      />

      <TextField
        fullWidth
        label="Description"
        variant="outlined"
        multiline
        value={description}
        onChange={(e) => setDescription(e.target.value)}
        required
        margin="normal"
        InputProps={{
          style: { borderRadius: 8 },
        }}
      />

      <TextField
        fullWidth
        label="Image URL"
        variant="outlined"
        value={image}
        onChange={(e) => setImage(e.target.value)}
        required
        margin="normal"
        InputProps={{
          style: { borderRadius: 8 },
        }}
      />

      <TextField
        fullWidth
        label="Genre"
        variant="outlined"
        value={genre}
        onChange={(e) => setGenre(e.target.value)}
        required
        margin="normal"
        InputProps={{
          style: { borderRadius: 8 },
        }}
      />

      <TextField
        fullWidth
        label="Release Date"
        type="date"
        variant="outlined"
        value={releaseDate}
        onChange={(e) => setReleaseDate(e.target.value)}
        required
        margin="normal"
        InputProps={{
          style: { borderRadius: 8 },
        }}
        InputLabelProps={{
          shrink: true
        }}
      />

      <TextField
        fullWidth
        label="Rating"
        type="number"
        variant="outlined"
        value={rating}
        onChange={(e) => setRating(e.target.value)}
        step="0.1"
        margin="normal"
        InputProps={{
          style: { borderRadius: 8 },
        }}
      />

      <Button
        type="submit"
        fullWidth
        variant="contained"
        color="primary"
        disabled={isLoading}
        sx={{
          mt: 2,
          borderRadius: 20,
          py: 1.5,
        }}
        startIcon={isLoading && <CircularProgress size={20} />}
      >
        {isLoading ? 'Adding...' : 'Add Movie'}
      </Button>
    </Box>
  );
};

export default TovarForm;
